import React from "react";
import { Breadcrumb, Layout, message } from "antd";
import moment from "moment";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import SideBar from "../home/_sideBar";
import ItemTable from "./_item_table.jsx";
import ItemForm from "./_item_form.jsx";
import axios from "axios";
const { Content } = Layout;
class Item extends React.Component {

  constructor(props) {
    super(props);
    console.log("constructor");
    this.state = {
      items: [],
      editItem: [],
    };
  }

  // リストのデータを表示条件
  componentDidMount() {
    axios
      .get("api/v1/item.json")
      .then((response) => {
        console.log("update soe lynn aung", response.data.items);
        response.data.forEach((item) => {
          const newItem = {
            key: item.id,
            id: item.id,
            name: item.name,
            price: item.price,
            size: item.size,
            country: item.country,
            manafacture_date: item.manafacture_date,
            email: item.email
          };

          this.setState((prevState) => ({
            items: [...prevState.items, newItem],
          }));
        });
      })
      .catch((error) => console.log(error));
  }

  onFinish = (item) => {
    // 挿入条件と更新条件の管理
    // itemのidがない場合は 挿入条件をします。
    // 挿入条件
    if (item.id == undefined) {
      fetch("/api/v1/item", {
        method: "post",
        body: JSON.stringify(item),
        headers: { "Content-Type": "application/json" },
      })
        .then((res) => res.json())
        .then((response) => {
          const data = {
            key: response.id,
            id: response.id,
            name: response.name,
            price: response.price,
            size: response.size,
            country: response.country,
            manafacture_date: response.manafacture_date,
            email: response.email
          };
          this.setState((prevState) => ({
            items: [...prevState.items, data],
          }));
          message.success(" 新しいアイテムが成功を生み出しました。");
        });
    }
    // itemのidがある場合は 更新条件をします。
    // 更新条件
    else {
      fetch(`/api/v1/item/update/${ item.id }`, {
        method: "post",

        body: JSON.stringify(item),

        headers: { "Content-Type": "application/json" },
      })
        .then((res) => res.json())
        .then(() => {
          message.success(" 更新が成功しました。");
        });
    }
  };

  // 削除条件
  deleteItem = (id) => {
    const url = `api/v1/items/${ id }`;
    fetch(url, {
      method: "delete",
    })
      .then(() => {
        this.setState({
          items: this.state.items.filter((item) => item.key !== id),
        });
        message.success(" アイテム削除の成功しました。");
      })
      .catch((error) => console.log(error));
  };

  // 提出された値をフォームに設定するために使用する。
  formRef = React.createRef();
  // 更新したいデータを更新フォームに設定します。
  edit = (record) => {
    this.formRef.current?.setFieldsValue({
      id: record.id,
      name: record.name,
      price: record.price,
      size: record.size,
      country: record.country,
      manafacture_date: moment(record.manafacture_date),
      email: record.email,
      remark: record.remark
    });
  };

  render() {
    return (
      <Layout style = {{ minHeight: "100vh" }}>
        <SideBar />
        <Layout className = "site-layout">
          <Content style = {{ margin: "0 16px" }}>
            <Breadcrumb style = {{ margin: "16px 0" }}>
              <Breadcrumb.Item>{ I18n.t("home.home") }</Breadcrumb.Item>
              <Breadcrumb.Item>{ I18n.t("home.menu1.side2") }</Breadcrumb.Item>
            </Breadcrumb>
            <div
              className = "site-layout-background"
              style={{ padding: 24, minHeight: 360 }}
            >

              {/* 挿入フォームのコンポーネントを呼び出す。 */}
              <ItemForm
                onFinish = { this.onFinish }
                editItems = { this.state.editItem }
                componentEdit = { this.formRef }
              />

              {/* テーブルのコンポーネントを呼び出す。 */}
              <ItemTable
                items = { this.state.items }
                edit = { this.edit }
                delete = { this.deleteItem }
              />

            </div>
          </Content>
        </Layout>
      </Layout>
    );
  }
}
export default Item;
