import React from "react";
import { Table, Space } from 'antd';
import axios from "axios";
class ItemTable extends React.Component {

      handleDelete = (itemId) => {
        fetch(`/api/v1/items/${itemId}`, { method: 'delete' }).
          then((response) => {
            alert('Post deleted successfully')
            // this.fetchPostsList();
          });
      }
    
    
    render() { 
        const columns = [
            {
              title: 'Item Name',
              dataIndex: 'name',
              filters: [
                {
                  text: 'Joe',
                  value: 'Joe',
                },
                {
                  text: 'Jim',
                  value: 'Jim',
                },
                {
                  text: 'Submenu',
                  value: 'Submenu',
                  children: [
                    {
                      text: 'Green',
                      value: 'Green',
                    },
                    {
                      text: 'Black',
                      value: 'Black',
                    },
                  ],
                },
              ],
              // specify the condition of filtering result
              // here is that finding the name started with `value`
              onFilter: (value, record) => record.name.indexOf(value) === 0,
              sorter: (a, b) => a.name.length - b.name.length,
              sortDirections: ['descend'],
            },
            {
              title: 'Item Price',
              dataIndex: 'price',
              defaultSortOrder: 'descend',
              sorter: (a, b) => a.age - b.age,
            },

            {
                title: 'Item Size',
                dataIndex: 'size',
                defaultSortOrder: 'descend',
                sorter: (a, b) => a.age - b.age,
              },

            {
                title: 'Manufactured Country',
                dataIndex: 'country',
                defaultSortOrder: 'descend',
                sorter: (a, b) => a.age - b.age,
              },

            {
              title: 'Manufactured Date',
              dataIndex: 'manafacture_date',
              filters: [
                {
                  text: 'London',
                  value: 'London',
                },
                {
                  text: 'New York',
                  value: 'New York',
                },
              ],
              onFilter: (value, record) => record.address.indexOf(value) === 0,
            },
            {
              title: 'Action',
              key: 'action',
              render: () => (
                <Space size="middle">
                  <a>Edit</a>
                  <a onClick={() => this.handleDelete(this.props.items) }>Delete</a>
                </Space>
              ),
            },
          ];
          console.log('dd', this.props.items);
          
          
          function onChange(pagination, filters, sorter, extra) {
            console.log('params', pagination, filters, sorter, extra);
          }

          
        return(
        <div>
            <Table columns={columns} dataSource={this.props.items} />
        </div>
        );
    }
}
 
export default ItemTable;
