import React, { useState, Fragment } from "react";
import {
  Form,
  Input,
  Button,
  Select,
  DatePicker,
  InputNumber,
  Modal,
  Radio,
  Table,
} from "antd";
import I18n from "i18n-js";
const { Search } = Input;
import moment from "moment";
const ItemForm = ({
  onFinish,
  componentEdit,
  itemSize,
  company,
  excelExport,
}) => {
  // モーダルフォームの管理
  const [isModalVisible, setIsModalVisible] = useState(false);

  // popupフィールドのテーブルにデータを設定する。
  const [tableData, setColumnData] = useState(null);

  //  popupフィールドのテーブルにデータの中で選んだデータをフォームの社会項目に設定する。
  const onChange = (value) => {
    componentEdit.current?.setFieldsValue({
      company_name: value.company_name,
      company_id: value.id,
    });
    // モーダルフォームを閉じる機能をする。
    setIsModalVisible(false);
  };

  // モーダルフォームを表示する機能をする。
  const showModal = () => {
    setIsModalVisible(true);
    console.log("carray company data = ", company);
    setColumnData(company);
  };

  // モーダルフォームを閉じる機能をする。
  const handleOk = () => {
    setIsModalVisible(false);
  };

  // モーダルフォームを閉じる機能をする
  const handleCancel = () => {
    setIsModalVisible(false);
  };

  // popupフィールドの設定するデータを検索するため設定する入力フィールド
  const searchField = (
    <Form.Item label={I18n.t("item.company")}>
      <Input
        key="1"
        name="searchData"
        onChange={(e) => handleSearch(e.target.value ? [e.target.value] : [])}
      ></Input>
    </Form.Item>
  );

  // popupフィールドのデータを検索した後結果を保つするためこのarrayを作りました。
  const companies = [];

  // popupフィールドの設定するデータを検索する。
  const handleSearch = (searchData) => {
    console.log("searchData", searchData);
    for (var i = 0; i < company.length; i++) {
      if (
        company[i].company_name
          ?.toLowerCase()
          .includes(searchData.toString()?.toLowerCase())
      ) {
        companies.push(company[i]);
      }
    }
    // popupフィールドのデータを検索した後結果をpopupフィールドのテーブルに表示する。
    setColumnData(companies);
  };

  // popupフィールドの設定するデータテーブルをつくる。
  const columns = [
    {
      title: "アクション",
      key: "action",
      render: (_text, record) => (
        <Fragment>
          <Radio
            key={record.company_id}
            value={record.company_id}
            onChange={() => onChange(record)}
          ></Radio>
        </Fragment>
      ),
    },
    {
      title: I18n.t("item.company"),
      dataIndex: "company_name",
      key: "company_id",
    },
  ];

  return (
    <div>
      {/* フォーム設定 */}
      <Form
        labelCol={{ span: 6 }}
        wrapperCol={{ span: 14 }}
        layout="horizontal"
        onFinish={onFinish}
        ref={componentEdit}
      >
        {/* 番号フィールド */}
        <Form.Item label=" " name="id" style={{ display: "none" }}>
          <Input />
        </Form.Item>

        {/* アイテム名前フィールド */}
        <Form.Item
          label={I18n.t("item.itemName")}
          name="name"
          rules={[
            {
              required: true,
              message: I18n.t("validation.message"),
            },
          ]}
        >
          <Input style={{ width: "60%" }} />
        </Form.Item>

        {/* アイテム価格フィールド */}
        <Form.Item
          label={I18n.t("item.itemPrice")}
          name="price"
          rules={[
            {
              required: true,
              message: I18n.t("validation.message"),
            },
          ]}
        >
          <InputNumber style={{ width: "60%" }} />
        </Form.Item>

        {/* アイテムサイズフィールド */}
        <Form.Item
          label={I18n.t("item.itemSize")}
          name="size_id"
          rules={[
            {
              required: true,
              message: I18n.t("validation.message"),
            },
          ]}
        >
          <Select defaultValue="一つ選択してください" style={{ width: "60%" }}>
            {itemSize.map((value) => (
              <Select.Option key={value.id} value={value.id}>
                {value.size}
              </Select.Option>
            ))}
          </Select>
        </Form.Item>

        {/* アイテムの製造国フィールド */}
        <Form.Item
          label={I18n.t("item.itemCountry")}
          name="country"
          rules={[
            {
              required: true,
              message: I18n.t("validation.message"),
            },
          ]}
        >
          <Input style={{ width: "60%" }} />
        </Form.Item>

        {/* 会社リストフィールド */}
        <Form.Item
          label={I18n.t("item.company")}
          name="company_name"
          rules={[
            {
              required: true,
              message: I18n.t("validation.message"),
            },
          ]}
        >
          <Search
            allowClear
            enterButton="検索"
            size="medium"
            style={{ width: "60%" }}
            onSearch={showModal}
          />
        </Form.Item>

        {/* 製造日フィールド */}
        {/* <Form.Item label={I18n.t("item.itemDate")} name="manafacture_date">
          <DatePicker
            disabledDate={disabledDate}
            style={{ width: "60%" }}
            placeholder="日付を選択してください"
          />
        </Form.Item> */}

        {/* メールフィールド */}
        <Form.Item
          label={I18n.t("item.email")}
          name="email"
          rules={[
            {
              type: "email",
              message: I18n.t("validation.email"),
            },
            {
              required: true,
              message: I18n.t("validation.message"),
            },
          ]}
        >
          <Input style={{ width: "60%" }} />
        </Form.Item>

        {/* リマークフィールド */}
        <Form.Item
          label={I18n.t("item.remark")}
          name="remark"
          rules={[
            {
              max: 20,
              message: I18n.t("validation.remark"),
            },
          ]}
        >
          <Input.TextArea style={{ width: "60%", resize: "none" }} />
        </Form.Item>

        {/* companiesのテーブルIDを運ぶ */}
        <Form.Item label=" " name="company_id" style={{ display: "none" }}>
          <Input />
        </Form.Item>

        {/* 保存ボタン */}
        <Form.Item wrapperCol={{ offset: 6 }}>
          <Button type="primary" htmlType="submit">
            {I18n.t("common.button.save")}
          </Button>

          <Button
            style={{ marginLeft: 30 }}
            type="primary"
            htmlType="button"
            onClick={excelExport}
          >
            {I18n.t("common.button.excel-export")}
          </Button>
        </Form.Item>
      </Form>

      {/* ポップアップモーダル */}
      <Modal
        title="会社一連"
        visible={isModalVisible}
        // モーダルフォームを閉じる機能を呼び出す。
        onOk={handleOk}
        // モーダルフォームを閉じる機能を呼び出す。
        onCancel={handleCancel}
        footer={[<Button onClick={handleCancel}>オーケー</Button>]}
      >
        {/* 検索入力フィールドを設定する */}
        {searchField}
        <br></br>
        {/* popupに表示するテーブルリスト */}
        <Table
          // rowSelection={rowSelection}
          columns={columns}
          dataSource={tableData}
          pagination={{ pageSize: 5 }}
        />
      </Modal>
    </div>
  );
};

export default ItemForm;
