import React, { Fragment } from "react";
import { Table, message, Popconfirm, Button } from "antd";
import I18n from "i18n-js";
class ItemTable extends React.Component {
  
  constructor(props) {
    super(props);
    this.state = {
      sortedInfo: {
        columnKey: 'name'
       },
    };
  }

  handleChange = (sorter) => {
    console.log('Various parameters', sorter);
    this.setState({
      sortedInfo: sorter,
    });
  };

  clearAll = () => {
    this.setState({
      sortedInfo: null,
    });
  };

  render() {
    const columns = [
      {
        title: I18n.t("item.itemName"),
        dataIndex: "name",
        key: "name",
        defaultSortOrder: 'descend',
        sorter: (a, b) => a.name.length - b.name.length,
        sortOrder: this.state.sortedInfo.columnKey === 'name' && this.sortedInfo.order,
        ellipsis: true,
      },
      {
        title: I18n.t("item.itemPrice"),
        dataIndex: "price",
        defaultSortOrder: 'descend',
        sorter: (a, b) => a.price - b.price,
      },

      {
        title: I18n.t("item.itemSize"),
        dataIndex: "size",
      },

      {
        title: I18n.t("item.itemCountry"),
        dataIndex: "country",
      },

      {
        title: I18n.t("item.itemDate"),
        dataIndex: "manafacture_date",
        // onFilter: (value, record) => record.address.indexOf(value) === 0,
      },
      {
        title: I18n.t("item.email"),
        dataIndex: "email",
        // onFilter: (value, record) => record.address.indexOf(value) === 0,
      },
      {
        title: "",
        key: "action",
        render: (_text, record) => (
          <Fragment>
            <Popconfirm
              title = "削除してもよろしいですか？"
              onConfirm = {() => this.props.delete(record.id)}
              okText = "はい"
              cancelText = "いいえ"
            >
              <Button type = "danger"> { I18n.t("common.button.delete") } </Button>
            </Popconfirm>
            <Button
              onClick = { () => this.props.edit(record) }
              style = {{ marginLeft: 12 }}
              type = "primary"
            >
              { I18n.t("common.button.edit") } {" "}
            </Button>
          </Fragment>
        ),
      },
    ];

    return (
      <div>
        {/* 表示テーブル */}
        <Table columns = { columns } dataSource = { this.props.items } />
      </div>
    );
  }
}

export default ItemTable;
