import React, { useState } from 'react';
import {
    Form,
    Input,
    Button,
    Select,
    DatePicker,
    InputNumber,
  } from 'antd';
import "../../bundles/i18n/ja.js";
class ItemForm extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      name: '',
      price: '',
      size: '',
      country: '',
      manafacture_date: ''
    }
  }


  createItemRequest = (value) => {

    console.log('create');
    console.log('this.state', value);
    fetch('/api/v1/item', {
      method: 'post',
      body: JSON.stringify(this.state),
      headers: { 'Content-Type': 'application/json' },
    }).then((response) => {
      alert('item created successfully');
      location.href = '/items';
    });
  }

  form = Form.useForm();

    render() { 

      const onFinish = values => {
        console.log('Finish:', values);
      };
      

        return(
        <div>
               <Form
               form={form}
        labelCol={{ span: 4 }}
        wrapperCol={{ span: 14 }}
        layout="horizontal"
        onFinish={onFinish}
      >

        <Form.Item label="Item Name" name = "name" rules={[
          {
            required: true,
            message: 'The name is required.',
          },
        ]}>
          <Input onChange={(e) => {
            value = e.target.value;
          }}/>
        </Form.Item>

        <Form.Item label="Item Price">
          <InputNumber />
        </Form.Item>

        <Form.Item label="item Size">
          <Select>
            <Select.Option value="small" >Small</Select.Option>
            <Select.Option value="medium">Medium</Select.Option>
            <Select.Option value="large" >Large</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item label="Manufactured Country">
          <Input />
        </Form.Item>
        
        <Form.Item label="Manufactured Date">
          <DatePicker />
        </Form.Item>

        <Form.Item label=" ">
        <Button type="primary" htmlType="submit">Save</Button>
        </Form.Item>

      </Form>
        </div>
        );
    }
}
 
export default ItemForm;
