import React, { useState } from "react";
import { Form, Input, Button, Select, DatePicker, InputNumber } from "antd";
import "../../bundles/i18n/ja.js";
function ItemUpdateForm() {
  const [form] = Form.useForm();
  return (
    <div>
      <Form
        name="userForm"
        form={form}
        labelCol={{ span: 4 }}
        wrapperCol={{ span: 14 }}
        layout="horizontal"
      >
        <Form.Item
          label="Item Name"
          name="name"
          rules={[
            {
              required: true,
              message: "This field is required.",
            },
          ]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label="Item Price"
          name="price"
          rules={[
            {
              required: true,
              message: "This filed is required.",
            },
          ]}
        >
          <InputNumber value={this.props.editItems.name} />
        </Form.Item>

        <Form.Item
          label="item Size"
          name="size"
          rules={[
            {
              required: true,
              message: "This filed is required.",
            },
          ]}
        >
          <Select>
            <Select.Option value="small">Small</Select.Option>
            <Select.Option value="medium">Medium</Select.Option>
            <Select.Option value="large">Large</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item
          label="Manufactured Country"
          name="country"
          rules={[
            {
              required: true,
              message: "This filed is required.",
            },
          ]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label="Manufactured Date"
          name="manafacture_date"
          rules={[
            {
              required: true,
              message: "This filed is required.",
            },
          ]}
        >
          <DatePicker />
        </Form.Item>

        <Form.Item label=" ">
          <Button type="primary" htmlType="submit">
            Save
          </Button>
        </Form.Item>
      </Form>
      <li>{this.props.editItems.name}</li>
    </div>
  );
}
export default ItemUpdateForm;
