import React, { useState } from "react";
import { Form, Input, Button, Select, DatePicker, InputNumber, Modal, Radio } from "antd";
import I18n from "i18n-js";
import moment from 'moment';
const ItemForm = ({ onFinish, componentEdit, itemSize, company }) => {
  // validateMessages = {
  //   number: {
  //     range: '${price} ${min} and ${max}文字である必要があります'
  //   },
  // };
  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  const disabledDate = (current) => {
    // Can not select days before today and today
    return current < moment().endOf('day');
  }
  
  return (
    <div>
      {/* form setting */}
      <Form
        labelCol={{ span: 6 }}
        wrapperCol={{ span: 14 }}
        layout="horizontal"
        onFinish={onFinish}
        ref={componentEdit}
      >
        {/* 番号フィールド */}
        <Form.Item label=" " name="id" style={{ display: "none" }}>
          <Input />
        </Form.Item>

        {/* アイテム名前フィールド */}
        <Form.Item
          label={I18n.t("item.itemName")}
          name="name"
          rules={[
            {
              required: true,
              message: "この項目は必須です。",
            },
          ]}
        >
          <Input style={{ width: "60%" }} />
        </Form.Item>
          
        {/* アイテム価格フィールド */}
        <Form.Item
          label={I18n.t("item.itemPrice")}
          name="price"
          rules={[
            {
              required: true,
              message: "この項目は必須です。",
            }
            // {
            //   min: 100, max: 100000
            // }
          ]}
        >
          <InputNumber style={{ width: "60%" }} />
        </Form.Item>
          
        {/* アイテムサイズフィールド */}
        <Form.Item
          label={I18n.t("item.itemSize")}
          name="size"
          rules={[
            {
              required: true,
              message: "この項目は必須です。",
            },
          ]}
        >
          <Select defaultValue="一つ選択してください" style={{ width: "60%" }}>
            {itemSize.map((value) => (
              <Select.Option key={value.id} value={value.id}>{value.size}</Select.Option>
            ))}
          </Select>
        </Form.Item>
          
        {/* アイテムの製造国フィールド */}
        <Form.Item
          label={I18n.t("item.itemCountry")}
          name="country"
          rules={[
            {
              required: true,
              message: "この項目は必須です。",
            },
          ]}
        >
          <Input style={{ width: "60%" }} />
        </Form.Item>
          
        <Form.Item
          label={I18n.t("item.company")}
          name="name"
          rules={[
            {
              required: true,
              message: "この項目は必須です。",
            },
          ]}
        >
          <Select onClick={showModal} defaultValue="一つ選択してください" style={{ width: "60%" }}>
            {/* {company.map((value) => (
              <Select.Option key={value.id} value={value.id}>{value.name}</Select.Option>
            ))} */}
          </Select>
        </Form.Item>
          
        {/* 製造日フィールド */}
        <Form.Item
          label={I18n.t("item.itemDate")}
          name="manafacture_date"
          rules={[
            {
              required: true,
              message: "この項目は必須です。",
            },
          ]}
        >
          <DatePicker disabledDate={disabledDate} style={{ width: "60%" }} placeholder="日付を選択してください" />
        </Form.Item>

        <Form.Item
          label={I18n.t("item.email")}
          name="email"
          rules={[
            {
              type: 'email',
              message: "正しいメールアドレスに記入してください。",
            },
            {
              required: true,
              message: "この項目は必須です。",
            },
          ]}>
          <Input style={{ width: "60%" }} />
        </Form.Item>
          
        <Form.Item
          label={I18n.t("item.remark")}
          name="remark"
          rules={[
            {
              max: 20,
              message: "20文字以内を入力してください。",
            }
          ]}
        >
          <Input.TextArea style={{ width: "60%", resize: "none" }} />
        </Form.Item>

        {/* 保存ボタン */}
        <Form.Item label=" ">
          <Button type="primary" htmlType="submit">
            {I18n.t("common.button.save")}
          </Button>
        </Form.Item>
      </Form>

      {/* popup Modal */}
      <Modal title="Basic Modal" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>
      <Radio.Group name="radiogroup" defaultValue={1}>
        {company.map((value) => (
          <Radio key={value.id} value={ value.id }>{value.name}</Radio>
        ))}
      </Radio.Group>
      </Modal>
    </div>
  );
};

export default ItemForm;
