import React, { useState } from 'react';
import {
    Form,
    Input,
    Button,
    Select,
    DatePicker,
    InputNumber,
  } from 'antd';
import "../../bundles/i18n/ja.js";
class ItemForm extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      name: '',
      price: '',
      size: '',
      country: '',
      manafacture_date: ''
    }
  }


  createItemRequest = (event) => {
    console.log('this.state', this.state);
    fetch('/api/v1/item', {
      method: 'post',
      body: JSON.stringify(this.state),
      headers: { 'Content-Type': 'application/json' },
    }).then((response) => {
      alert('item created successfully');
      location.href = '/';
    });
  }


    render() {      
        const {name, price, size, country, manafacture_date} = this.state;

        return(
        <div>
               <Form
        labelCol={{ span: 4 }}
        wrapperCol={{ span: 14 }}
        layout="horizontal"
      >

        <Form.Item label="Item Name">
          <Input name='name' value={name} />
        </Form.Item>

        <Form.Item label="Item Price">
          <InputNumber type='text' name='price' value={price} />
        </Form.Item>

        <Form.Item label="item Size">
          <Select value={size}>
            <Select.Option value="small" name="size">Small</Select.Option>
            <Select.Option value="medium" name="size">Medium</Select.Option>
            <Select.Option value="large" name="size">Large</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item label="Manufactured Country">
          <Input type='text' name='country' value={country} />
        </Form.Item>
        
        <Form.Item label="Manufactured Date">
          <DatePicker type='text' name='manafacture_date' value={manafacture_date} />
        </Form.Item>

        <Form.Item label=" ">
        <Button type="primary" onClick={this.createItemRequest}>Save</Button>
        </Form.Item>

      </Form>
        </div>
        );
    }
}
 
export default ItemForm;
