import React, { useState } from "react";
import { Form, Input, Button, Select, DatePicker, InputNumber } from "antd";
import I18n from "i18n-js";
const [form] = Form.useForm();
class ItemForm extends React.Component {
  onReset = () => {
    form.resetFields();
  };
  render() {
    return (
      <div>
        <Form
          labelCol={{ span: 4 }}
          wrapperCol={{ span: 14 }}
          layout="horizontal"
          onFinish={this.props.onFinish}
          ref={this.props.componentEdit}
        >
          <Form.Item label=" " name="id" style={{ display: "none" }}>
            <Input />
          </Form.Item>

          <Form.Item
            label={I18n.t("item.itemName")}
            name="name"
            rules={[
              {
                required: true,
                message: "This field is required.",
              },
            ]}
          >
            <Input />
          </Form.Item>

          <Form.Item
            label={I18n.t("item.itemPrice")}
            name="price"
            rules={[
              {
                required: true,
                message: "This filed is required.",
              },
            ]}
          >
            <InputNumber />
          </Form.Item>

          <Form.Item
            label={I18n.t("item.itemSize")}
            name="size"
            rules={[
              {
                required: true,
                message: "This filed is required.",
              },
            ]}
          >
            <Select>
              <Select.Option value="small">Small</Select.Option>
              <Select.Option value="medium">Medium</Select.Option>
              <Select.Option value="large">Large</Select.Option>
            </Select>
          </Form.Item>

          <Form.Item
            label={I18n.t("item.itemCountry")}
            name="country"
            rules={[
              {
                required: true,
                message: "This filed is required.",
              },
            ]}
          >
            <Input />
          </Form.Item>

          <Form.Item
            label={I18n.t("item.itemDate")}
            name="manafacture_date"
            rules={[
              {
                required: true,
                message: "This filed is required.",
              },
            ]}
          >
            <DatePicker />
          </Form.Item>

          <Form.Item label=" ">
            <Button type="primary" htmlType="submit">
              {I18n.t("common.button.save")}
            </Button>
            <Button htmlType="button" onClick={onReset}>
            {I18n.t("common.button.cancel")}
            </Button>
          </Form.Item>
        </Form>
      </div>
    );
  }
}

export default ItemForm;
