    //   title="Login User"
    //   visible={isModalVisible}
    //   // モーダルフォームを閉じる機能を呼び出す。
    //   onOk={handleOk}
    //   // モーダルフォームを閉じる機能を呼び出す。
    //   onCancel={handleCancel}
    //   // モーダルフォームのフッターにオーケーボタンを設定する。
    //   footer={[<Button onClick={handleCancel}>オーケー</Button>]}
    // >
    //   <Form
    //     name="basic"
    //     labelCol={{ span: 8 }}
    //     wrapperCol={{ span: 16 }}
    //     initialValues={{ remember: true }}
    //     onFinish={onFinish}
    //     autoComplete="off"
    //   >
    //     <Form.Item
    //       label={I18n.t("item.email")}
    //       name="email"
    //       rules={[
    //         {
    //           type: "email",
    //           message: I18n.t("validation.email"),
    //         },
    //         {
    //           required: true,
    //           message: I18n.t("validation.message"),
    //         },
    //       ]}
    //     >
    //       <Input style={{ width: "60%" }} />
    //     </Form.Item>

    //     <Form.Item
    //       label="Password"
    //       name="encrypted_password"
    //       rules={[{ required: true, message: "Please input your password!" }]}
    //     >
    //       <Input.Password style={{ width: "60%" }} />
    //     </Form.Item>

    //     <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
    //       <Button type="primary" htmlType="submit">
    //         Register
    //       </Button>
    //     </Form.Item>
    //   </Form>
    // </Modal>;
  };

  return (
    <Form
      name="basic"
      labelCol={{ span: 8 }}
      wrapperCol={{ span: 16 }}
      initialValues={{ remember: true }}
      onFinish={onFinish}
      autoComplete="off"
    >
      <Form.Item
        label="Username"
        name="user_name"
        rules={[{ required: true, message: "Please input your username!" }]}
      >
        <Input style={{ width: "60%" }} />
      </Form.Item>

      <Form.Item
        label={I18n.t("item.email")}
        name="email"
        rules={[
          {
            type: "email",
            message: I18n.t("validation.email"),
          },
          {
            required: true,
            message: I18n.t("validation.message"),
          },
        ]}
      >
        <Input style={{ width: "60%" }} />
      </Form.Item>

      <Form.Item
        label="Password"
        name="encrypted_password"
        rules={[{ required: true, message: "Please input your password!" }]}
      >
        <Input.Password style={{ width: "60%" }} />
      </Form.Item>

      <Form.Item
        label="Confirm Password"
        name="re_password"
        rules={[{ required: true, message: "Please input your password!" }]}
      >
        <Input.Password style={{ width: "60%" }} />
      </Form.Item>

      <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
        <Button type="primary" htmlType="submit">
          Register
        </Button>

        <Button type="primary" onClick={login} style={{ marginLeft: "10px" }}>
          Login
        </Button>
      </Form.Item>
    </Form>
  );
};
export default UserForm;
