import React, { Fragment } from "react";
import { Table, message, Popconfirm } from 'antd';
import axios from "axios";
import ItemForm from "./_item_form.jsx"
class ItemTable extends React.Component {
  formRef = React.createRef();
      // call form load
      reloadItem = () => {
        console.log('reload2');
      this.setState({ items: [] });
      this.props.function
      };

      // for edit

      edit(record){
        this.formRef.current?.setFieldsValue({
          name: record.name,
          price: 3,
          size: 2,
          country: 'Asia'
          //manafacture_date: record.manafacture_date
          // price: this.state.editItem[1],
          // size: this.state.editItem[2],
          // country: this.state.editItem[3],
         // manafacture_date: this.props.editItems.manafacture_date
          // country: this.props.editItems.country,
          // manafacture_date: this.props.editItems.manafacture_date
        });
        console.log("fffff",this.formRef);
          // axios.get(`/api/v1/item/${id}`)
          // .then((response) => {
          //   this.setState((prevState) => ({
          //     editItem: [...prevState.editItem, response.data],
              
          //   }));  
          //   console.log("edit123",this.state.editItem)
          // })
          // .catch((error) => console.log(error));
          // this.editItem: record;
          // this.setState({ editItem }) 
          // console.log("edit123",this.state.editItem)
      }
    
      // update request
      updatePostRequest = (event) => {
        fetch(`/api/v1/posts/${this.state.id}`, {
          method: 'put',
          body: JSON.stringify(this.state),
          headers: { 'Content-Type': 'application/json' },
        }).then((response) => {
          alert('Post updated successfully');
          location.href = '/';
        });
      }

    
    render() { 
        
          console.log('dd', this.props.items);
          
         const columns = [
            {
              title: 'Item Name',
              dataIndex: 'name',
              filters: [
                {
                  text: 'Joe',
                  value: 'Joe',
                },
                {
                  text: 'Jim',
                  value: 'Jim',
                },
                {
                  text: 'Submenu',
                  value: 'Submenu',
                  children: [
                    {
                      text: 'Green',
                      value: 'Green',
                    },
                    {
                      text: 'Black',
                      value: 'Black',
                    },
                  ],
                },
              ],
              // specify the condition of filtering result
              // here is that finding the name started with `value`
              onFilter: (value, record) => record.name.indexOf(value) === 0,
              sorter: (a, b) => a.name.length - b.name.length,
              sortDirections: ['descend'],
            },
            {
              title: 'Item Price',
              dataIndex: 'price',
              defaultSortOrder: 'descend',
              sorter: (a, b) => a.age - b.age,
            },
    
            {
                title: 'Item Size',
                dataIndex: 'size',
                defaultSortOrder: 'descend',
                sorter: (a, b) => a.age - b.age,
              },
    
            {
                title: 'Manufactured Country',
                dataIndex: 'country',
                defaultSortOrder: 'descend',
                sorter: (a, b) => a.age - b.age,
              },
    
            {
              title: 'Manufactured Date',
              dataIndex: 'manafacture_date',
              filters: [
                {
                  text: 'London',
                  value: 'London',
                },
                {
                  text: 'New York',
                  value: 'New York',
                },
              ],
              onFilter: (value, record) => record.address.indexOf(value) === 0,
            },
            {
              title: "",
              key: "action",
              render: (_text, record) => (
                <Fragment>
                <Popconfirm title="Are you sure to delete this item?" onConfirm={() => this.props.delete(record.id)} okText="Yes" cancelText="No">
                  <a href="#" type="danger">
                    Delete{" "}
                  </a>
                </Popconfirm>
                <a href="#" type="primary" onClick = {() => this.edit(record)} >
                 Edit{" "}
                </a>
                </Fragment>
              ),
            },
            
          ];
          
          function onChange(pagination, filters, sorter, extra) {
            console.log('params', pagination, filters, sorter, extra);
          }

          
        return(
        <div>
            <Table columns={columns} dataSource={this.props.items} />
            <ItemForm 
                   
                   componentEdit = {this.formRef}/>  
        </div>
        );
    }
}
 
export default ItemTable;
