import React from "react";
import { Breadcrumb, Layout, message } from "antd";
import moment from 'moment';
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import SideBar from "../home/_sideBar";
import ItemTable from "./_item_table.jsx";
import ItemForm from "./_item_form.jsx"
import ItemUpdateForm from "./_item_update_form.jsx"
import axios from "axios";
const { Content } = Layout;
class Item extends React.Component { 
  formRef = React.createRef();
  constructor(props) {
    super(props);
    console.log("constructor");
    this.state = { 
      items: [], 
      editItem:[]
    };
  }
  
  componentDidMount() {
    axios.get("api/v1/item.json")
      .then((response) => {
        console.log("dd", response.data);
        response.data.forEach((item) => {
          const newItem = {
            key: item.id,
            id: item.id,
            name: item.name,
            price: item.price,
            size: item.size,
            country: item.country,
            manafacture_date: item.manafacture_date
          };


          this.setState((prevState) => ({
            items: [...prevState.items, newItem],
          }));
        });
      })
      .catch((error) => console.log(error));
  };

  //insert
  onFinish = values => {
    console.log('Finish:', values.id);
    if(values.id == undefined){
      fetch('/api/v1/item', {

        method: 'post',
  
        body: JSON.stringify(values),
  
        headers: { 'Content-Type': 'application/json' },
        
      })
      .then((res) => res.json())
    .then((response) => {

      message.success("新しいアイテムが成功を生み出しました。");

      location.href = '/items';

    });
    }
    
    else{
    fetch(`/api/v1/item/update/${values.id}`, {

      method: 'post',

      body: JSON.stringify(values),

      headers: { 'Content-Type': 'application/json' },
      
    })
    .then((res) => res.json())
    .then((response) => {
     

      message.success("update success。");

      location.href = '/items';

    });
   }

  };

  
  deleteItem = (id) => {
    const url = `api/v1/items/${id}`;
    // alert(url);
    fetch(url, {
      method: "delete",
    })
  .then((response) => {
    message.success("アイテム削除の成功しました。");
   location.href = '/items'; 
  })
  .catch((error) => console.log(error));
  };

  // show data for update

  edit=(record)=>{
    this.formRef.current?.setFieldsValue({
      id: record.id,
      name: record.name,
      price: record.price,
      size:  record.size,
      country:  record.country,
      manafacture_date: moment(record.manafacture_date)
      //manafacture_date: record.manafacture_date
      // price: this.state.editItem[1],
      // size: this.state.editItem[2],
      // country: this.state.editItem[3],
     // manafacture_date: this.props.editItems.manafacture_date
      // country: this.props.editItems.country,
      // manafacture_date: this.props.editItems.manafacture_date
    });
    console.log("fffff",this.formRef);
      // axios.get(`/api/v1/item/${id}`)
      // .then((response) => {
      //   this.setState((prevState) => ({
      //     editItem: [...prevState.editItem, response.data],
          
      //   }));  
      //   console.log("edit123",this.state.editItem)
      // })
      // .catch((error) => console.log(error));
      // this.editItem: record;
      // this.setState({ editItem }) 
      // console.log("edit123",this.state.editItem)
  }

//   renderElement(){
//     if(this.state.editItem.includes(id)){
//       alert('insert');
//     }
//     else{
//       alert('update');
//     }
//     //    return <Text>data</Text>;
//     // return null;
//  }


// componentDidUpdate(){
//   this.formRef.current?.setFieldsValue({

//     name: this.state.editItem[0],
//     price: this.state.editItem[1],
//     size: this.state.editItem[2],
//     country: this.state.editItem[3],
//    // manafacture_date: this.props.editItems.manafacture_date
//     // country: this.props.editItems.country,
//     // manafacture_date: this.props.editItems.manafacture_date
//   });
  // this.formRef.current.setFieldsValue({
  //   price: this.props.editItems?.price
  // });
  // this.formRef.current.setFieldsValue({
  //   size: this.props.editItems?.size
  // });
  // this.formRef.current.setFieldsValue({
  //   country: this.props.editItems?.country
  // });
  // this.formRef.current.setFieldsValue({
  //   manafacture_date: this.props.editItems?.manafacture_date
  // });
 //}


  render() {
    console.log('render'+this.state.items);

    return (
      <Layout style={{ minHeight: "100vh" }}>
        <SideBar />
        <Layout className="site-layout">
          <Content style={{ margin: "0 16px" }}>
            <Breadcrumb style={{ margin: "16px 0" }}>
              <Breadcrumb.Item>{I18n.t("home.home")}</Breadcrumb.Item>
              <Breadcrumb.Item>{I18n.t("home.menu1.side1")}</Breadcrumb.Item>
            </Breadcrumb>
            <div
              className="site-layout-background"
              style={{ padding: 24, minHeight: 360 }}
            >
                   <ItemForm 
                   onFinish = {this.onFinish}
                   editItems = {this.state.editItem}
                   componentEdit = {this.formRef}/>  
                   {/* <ItemUpdateForm 
                   editItems = {this.state.editItem}
                   edit = {this.edit}
                   /> */}
                   <ItemTable 
                   items = {this.state.items} 
                   edit = {this.edit}
                   delete = {this.deleteItem}
                   />
            </div>
          </Content>
        </Layout>
      </Layout>
    );
  }
}
export default Item;
