import React, { useState } from 'react';
import {
    Form,
    Input,
    Button,
    Select,
    DatePicker,
    InputNumber,
  } from 'antd';
import "../../bundles/i18n/ja.js";
class ItemForm extends React.Component {      
  formRef = React.createRef();

  componentDidUpdate(){
    this.formRef.current.setFieldsValue({
      name: this.props.editItems.name,
      price: this.props.editItems.price
    });
    // this.formRef.current.setFieldsValue({
    //   price: this.props.editItems?.price
    // });
    // this.formRef.current.setFieldsValue({
    //   size: this.props.editItems?.size
    // });
    // this.formRef.current.setFieldsValue({
    //   country: this.props.editItems?.country
    // });
    // this.formRef.current.setFieldsValue({
    //   manafacture_date: this.props.editItems?.manafacture_date
    // });
   }


  render() {
            return(
        <div>
           
      <Form
        labelCol={{ span: 4 }}
        wrapperCol={{ span: 14 }}
        layout="horizontal"
        onFinish={this.props.onFinish}
        ref={this.formRef} 
      >

        <Form.Item label="Item Name" name = "name" rules={[
          {
            required: true,
            message: 'This field is required.',
          },
        ]}>
          <Input />
        </Form.Item>

        <Form.Item label="Item Price" name = "price" rules={[
          {
            required: true,
            message: 'This filed is required.',
          },
        ]}>
          <InputNumber />
        </Form.Item>

        <Form.Item label="item Size" name = "size" rules={[
          {
            required: true,
            message: 'This filed is required.',
          },
        ]}>
          <Select>
            <Select.Option value="small" >Small</Select.Option>
            <Select.Option value="medium">Medium</Select.Option>
            <Select.Option value="large" >Large</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item label="Manufactured Country" name = "country" rules={[
          {
            required: true,
            message: 'This filed is required.',
          },
        ]}>
          <Input />
        </Form.Item>
        
        <Form.Item label="Manufactured Date" name = "manafacture_date" rules={[
          {
            required: true,
            message: 'This filed is required.',
          },
        ]}>
          <DatePicker />
        </Form.Item>

        <Form.Item label=" ">
        <Button type="primary" htmlType="submit">Save</Button>
        </Form.Item>
      </Form>
      <li>{this.props.editItems.name}</li>
        </div>
        );
      }
}
 
export default ItemForm;
