import React from "react";
import { Table } from 'antd';
import axios from "axios";
class ItemTable extends React.Component {
    constructor(props) {
        super(props);
        this.state = { items: [] };
      };

      componentDidMount() {
        console.log("didMount");
        axios
          .get("api/v1/item#index")
          .then((response) => {
            console.log("items = " + response);

            // start

            response.data.forEach((item) => {
                const newItem = {
                  key: item.id,
                  name: item.name,
                  price: item.price,
                  size: item.size,
                  country: item.country,
                  manafacture_date: item.manafacture_date
                }
           
            // end

            this.setState({
                items: newItem
            });
            console.log("soe soe"+ this.state.items);
        })
          })
          .catch((error) => console.log(error));
      };
    
    render() { 
        const columns = [
            {
              title: 'Item Name',
              dataIndex: 'name',
              filters: [
                {
                  text: 'Joe',
                  value: 'Joe',
                },
                {
                  text: 'Jim',
                  value: 'Jim',
                },
                {
                  text: 'Submenu',
                  value: 'Submenu',
                  children: [
                    {
                      text: 'Green',
                      value: 'Green',
                    },
                    {
                      text: 'Black',
                      value: 'Black',
                    },
                  ],
                },
              ],
              // specify the condition of filtering result
              // here is that finding the name started with `value`
              onFilter: (value, record) => record.name.indexOf(value) === 0,
              sorter: (a, b) => a.name.length - b.name.length,
              sortDirections: ['descend'],
            },
            {
              title: 'Item Price',
              dataIndex: 'age',
              defaultSortOrder: 'descend',
              sorter: (a, b) => a.age - b.age,
            },

            {
                title: 'Item Size',
                dataIndex: 'age',
                defaultSortOrder: 'descend',
                sorter: (a, b) => a.age - b.age,
              },

            {
                title: 'Manufactured Country',
                dataIndex: 'age',
                defaultSortOrder: 'descend',
                sorter: (a, b) => a.age - b.age,
              },

            {
              title: 'Manufactured Date',
              dataIndex: 'address',
              filters: [
                {
                  text: 'London',
                  value: 'London',
                },
                {
                  text: 'New York',
                  value: 'New York',
                },
              ],
              onFilter: (value, record) => record.address.indexOf(value) === 0,
            },
          ];

          function onChange(pagination, filters, sorter, extra) {
            console.log('params', pagination, filters, sorter, extra);
          }

        return(
        <div>
            <Table columns={columns} dataSource={this.state.items} onChange={onChange} />
        </div>
        );
    }
}
 
export default ItemTable;
