import React from "react";
import { Breadcrumb, Layout } from "antd";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import SideBar from "../home/_sideBar";
import ItemTable from "./_item_table.jsx";
import ItemForm from "./_item_form.jsx"
import ItemUpdateForm from "./_item_update_form.jsx"
import axios from "axios";
const { Content } = Layout;
class Item extends React.Component { 
  
  constructor(props) {
    super(props);
    console.log("constructor");
    this.state = { 
      items: [], 
      editItem:''
    };
  }
  
  componentDidMount() {
    
    axios.get("api/v1/item.json")
      .then((response) => {
        console.log("dd", response.data);
        response.data.forEach((item) => {
          const newEl = {
            key: item.id,
            id: item.id,
            name: item.name,
            price: item.price,
            size: item.size,
            country: item.country,
            manafacture_date: item.manafacture_date
          };


          this.setState((prevState) => ({
            items: [...prevState.items, newEl],
          }));
        });
      })
      .catch((error) => console.log(error));
  };

  onFinish = values => {
    console.log('Finish:', values);
    fetch('/api/v1/item', {
      method: 'post',
      body: JSON.stringify(values),
      headers: { 'Content-Type': 'application/json' },
    }).then((response) => {
      alert('item created successfully');
      location.href = '/items';
    });
  };

  deleteItem = (id) => {
    const url = `api/v1/items/${id}`;
    // alert(url);
    fetch(url, {
      method: "delete",
    })
  .then((response) => {
    location.href = '/items';   
  })
  .catch((error) => console.log(error));
  };

  // show data for update
  edit = (id) => {
      axios.get(`/api/v1/item/${id}`)
      .then((response) => {
        this.setState((prevState) => ({
          editItem: response.data,
        }));  
        console.log("edit123",this.state.editItem)
      })
      .catch((error) => console.log(error));
      // this.editItem: record;
      // this.setState({ editItem }) 
      // console.log("edit123",this.state.editItem)
  }

//   renderElement(){
//     if(this.state.editItem.includes(id)){
//       alert('insert');
//     }
//     else{
//       alert('update');
//     }
//     //    return <Text>data</Text>;
//     // return null;
//  }


  render() {
    console.log('render'+this.state.items);
    

    return (
      <Layout style={{ minHeight: "100vh" }}>
        <SideBar />
        <Layout className="site-layout">
          <Content style={{ margin: "0 16px" }}>
            <Breadcrumb style={{ margin: "16px 0" }}>
              <Breadcrumb.Item>{I18n.t("home.home")}</Breadcrumb.Item>
              <Breadcrumb.Item>{I18n.t("home.menu1.side1")}</Breadcrumb.Item>
            </Breadcrumb>
            <div
              className="site-layout-background"
              style={{ padding: 24, minHeight: 360 }}
            >
                   <ItemForm 
                   onFinish = {this.onFinish}
                   editItems = {this.state.editItem}/>  
                   <ItemUpdateForm 
                   editItems = {this.state.editItem}
                   edit = {this.edit}
                   />
                   <ItemTable 
                   items = {this.state.items} 
                   edit = {this.edit}
                   delete = {this.deleteItem}
                   />
            </div>
          </Content>
        </Layout>
      </Layout>
    );
  }
}
export default Item;
