import React from "react";
import { Breadcrumb, Layout, message, Input, Button, Space } from "antd";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import SideBar from "../home/_sideBar";
import ItemTable from "./_item_table.jsx";
import ItemForm from "./_item_form.jsx";
import axios from "axios";
import Highlighter from 'react-highlight-words';
import { SearchOutlined } from '@ant-design/icons';
import moment from "moment";
const { Content } = Layout;
class Item extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      size: [],
      company: [],
      searchText: '',
      searchedColumn: ''
    };
  }

  // リストのデータを表示条件
  componentDidMount() {
    this.setState({
      items: [],
      size: [],
      company: [],
      searchText: '',
      searchedColumn: ''
    });
    axios
      .get("api/v1/item.json")
      .then((response) => {
        this.setState(() => ({
          size: response.data.sizes,
          company: response.data.companies
        }));
        
        
        // popup データを表示するため
        response.data.companies.forEach((companyData) => {
          const newCompany = {
            key: companyData.id,
            id: companyData.id,
            name: companyData.company_name
          };
          this.setState((prevState) => ({
            company: [...prevState.company, newCompany],
          }));
        })
        
        // アイテムー管理テーブルリストに表示するため
        response.data.items.forEach((item) => {
            const newItem = {
              key: item.item_id,
              id: item.item_id,
              name: item.name,
              price: item.price,
              size: item.size,
              company: item.company_name,
              country: item.country,
              manafacture_date: item.manafacture_date,
              email: item.email,  
              company_id: item.company_id,  
              remark: item.remark,
              size_id: item.size_id
            };
            this.setState((prevState) => ({
              items: [...prevState.items, newItem],
            }));
        })
      })
      .catch((error) => console.log(error));
  }

  // フォームクリア機能
  clearForm() {
    this.formRef.current?.setFieldsValue({
      id: null,
      name: null,
      price: null,
      size_id: '一つ選択してください',
      country: null,
      manafacture_date: '日付を選択してください',
      company_name: '一つ選択してください',
      email: null,
      remark: null,
      company_id: null
    });
  }

  // データの挿入と更新
  onFinish = (item) => {
    // 挿入条件と更新条件の管理
    // itemのidがない場合は 挿入条件をします。
    // 挿入条件
    if (item.id == undefined) {
      fetch("/api/v1/item", {
        method: "post",
        body: JSON.stringify(item),
        headers: { "Content-Type": "application/json" },
      })
        .then((res) => res.json())
        .then((response) => {
          this.componentDidMount();
          message.success(" 新しいアイテムが成功を生み出しました。");
          this.clearForm();
        });
    }
    // itemのidがある場合は 更新条件をします。
    // 更新条件
    else {
      fetch(`/api/v1/item/update/${ item.id }`, {
        method: "post",
        body: JSON.stringify(item),
        headers: { "Content-Type": "application/json" },
      })
        .then((res) => res.json())
        .then(() => {
          this.componentDidMount();
          message.success(" 更新が成功しました。");
          this.clearForm();
        });
    }
  };

  // 削除条件
  deleteItem = (id) => {
    const url = `api/v1/items/${ id }`;
    fetch(url, {
      method: "delete",
    })
      .then(() => {
        this.setState({
          items: this.state.items.filter((item) => item.key !== id),
        });
        message.success(" アイテム削除の成功しました。");
      })
      .catch((error) => console.log(error));
  };

  // 提出された値をフォームに設定するために使用する。
  formRef = React.createRef();
  // 更新したいデータを更新フォームに設定します。
  edit = (record) => {
    this.formRef.current?.setFieldsValue({
      id: record.id,
      name: record.name,
      price: record.price,
      size_id: record.size_id,
      company_name: record.company,
      country: record.country,
      manafacture_date: moment(record.manafacture_date, "YYYY MM DD"),
      email: record.email,
      remark: record.remark,
      company_id: record.company_id
    });
  };

  // データのフィルタリングと検索する機能
  getColumnSearchProps = dataIndex => ({
    filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
      <div style={{ padding: 8 }}>

        {/* 検索するため入力フィールド */}
        <Input
          ref={node => {
            this.searchInput = node;
          }}
          placeholder={`Search ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
          onPressEnter={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
          style={{ marginBottom: 8, display: 'block' }}
        />

        <Space>
          {/* 検索するボタン */}
          <Button
            type="primary"
            onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
            icon={<SearchOutlined />}
            size="small"
            style={{ width: 90 }}
          >
            検索
          </Button>

          {/* リセットするボタン */}
          <Button onClick={() => this.handleReset(clearFilters)} size="small"
            style={{ width: 90 }}
          >
          リセット
          </Button>

          {/* フィルターするボタン */}
          <Button
            type="link"
            size="small"
            onClick={() => { confirm({ closeDropdown: false });
              this.setState({
                searchText: selectedKeys[0],
                searchedColumn: dataIndex,
              });
            }}
          >
            フィルター
          </Button>
        </Space>
      </div>
    ),
    filterIcon: filtered => <SearchOutlined style={{ color: filtered ? '#1890ff' : undefined }} />,
    onFilter: (value, record) =>
      record[dataIndex]
        ? record[dataIndex].toString().toLowerCase().includes(value.toLowerCase())
        : '',
    onFilterDropdownVisibleChange: visible => {
      if (visible) {
        setTimeout(() => this.searchInput.select(), 100);
      }
    },
    render: text =>
      this.state.searchedColumn === dataIndex ? (
        <Highlighter
          highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
          searchWords={[this.state.searchText]}
          autoEscape
          textToHighlight={text ? text.toString() : ''}
        />
      ) : (
        text
      ),
  });

  // 検索するボタンを押す時作業する機能
  handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    this.setState({
      searchText: selectedKeys[0],
      searchedColumn: dataIndex,
    });
  };

  // リセットするボタンを押す時作業する機能
  handleReset = clearFilters => {
    clearFilters();
    this.setState({ searchText: '' });
  };

  render() {
    return (
      <Layout style = {{ minHeight: "100vh" }}>
        <SideBar />
        <Layout className = "site-layout">
          <Content style = {{ margin: "0 16px" }}>
            <Breadcrumb style = {{ margin: "16px 0" }}>
              <Breadcrumb.Item>{ I18n.t("home.home") }</Breadcrumb.Item>
              <Breadcrumb.Item>{ I18n.t("home.menu1.side2") }</Breadcrumb.Item>
            </Breadcrumb>
            <div
              className = "site-layout-background"
              style={{ padding: 24, minHeight: 360 }}
            >

              {/* 挿入フォームのコンポーネントを呼び出す。 */}
              <ItemForm
                // 挿入と更新機能を呼び出してフォームコンポーネントを形成します。
                onFinish = { this.onFinish }
                // 更新したいデータを更新フォームに設定する機能を呼び出してフォームコンポーネントを形成します。
                componentEdit = { this.formRef }
                // 挿入と更新のフォームに設定するアイテムサイズマスターデータをフォームコンポーネントへ送る。
                itemSize = { this.state.size }
                // 挿入と更新のフォームに設定するアイテムサイズマスターデータをフォームコンポーネントへ送る。
                company= { this.state.company }
                
              />

              {/* テーブルのコンポーネントを呼び出す。 */}
              <ItemTable
                // アイテムー管理テーブルリストに表示するアイテムデータをテーブルコンポーネントへ送る。
                items = { this.state.items }
                // 更新したいデータを更新フォームに設定する機能をテーブルコンポーネントから呼び出す。
                edit = { this.edit }
                // アイテムー管理テーブルリストに表示するアイテムデータを消す機能をテーブルコンポーネントから呼び出す。
                delete = { this.deleteItem }
                // アイテムー管理テーブルリストに表示するアイテムデータを検索する機能をテーブルコンポーネントから呼び出す。
                filter = { this.getColumnSearchProps }
              />

            </div>
          </Content>
        </Layout>
      </Layout>
    );
  }
}
export default Item;
