import React, { useState } from 'react';
import {
    Form,
    Input,
    Button,
    Select,
    DatePicker,
    InputNumber,
  } from 'antd';
import "../../bundles/i18n/ja.js";

class ItemForm extends React.Component {
  constructor(props) {
    super(props);
    console.log("constructor");
    this.state = { 
      newItem: {
        name: "",
        price: "",
        size: "",
        country: "",
        manafacture_date: ""
      },
    };
    this.changeName = this.changeName.bind(this);
  }
  
  changeName = e => {
    this.setState({
      newItem: { name: e.target.value, price: this.state.newItem.price }
    });
  };
  // componentDidMount() {
  //   setValues({...values, name="Mario"});
  // }
  render() {
            return(
        <div>
           
               <Form
        labelCol={{ span: 4 }}
        wrapperCol={{ span: 14 }}
        layout="horizontal"
        onFinish={this.props.onFinish}
      >

        <Form.Item label="Item Name" name = "name" rules={[
          {
            required: true,
            message: 'This field is required.',
          },
        ]}>
          <Input value={this.state.newItem.name} onChange={this.changeName}/>
        </Form.Item>

        <Form.Item label="Item Price" name = "price" rules={[
          {
            required: true,
            message: 'This filed is required.',
          },
        ]}>
          <InputNumber />
        </Form.Item>

        <Form.Item label="item Size" name = "size" rules={[
          {
            required: true,
            message: 'This filed is required.',
          },
        ]}>
          <Select>
            <Select.Option value="small" >Small</Select.Option>
            <Select.Option value="medium">Medium</Select.Option>
            <Select.Option value="large" >Large</Select.Option>
          </Select>
        </Form.Item>

        <Form.Item label="Manufactured Country" name = "country" rules={[
          {
            required: true,
            message: 'This filed is required.',
          },
        ]}>
          <Input />
        </Form.Item>
        
        <Form.Item label="Manufactured Date" name = "manafacture_date" rules={[
          {
            required: true,
            message: 'This filed is required.',
          },
        ]}>
          <DatePicker />
        </Form.Item>

        <Form.Item label=" ">
        <Button type="primary" htmlType="submit">Save</Button>
        </Form.Item>
      </Form>
      <li>{this.props.editItems.name}</li>
        </div>
        );
      }
}
 
export default ItemForm;
