import React, { Fragment } from "react";
import { Table, message, Popconfirm } from 'antd';
import axios from "axios";
import I18n from "i18n-js";
class ItemTable extends React.Component {

      // call form load
      reloadItem = () => {
        console.log('reload2');
      this.setState({ items: [] });
      this.props.function
      };
    
      // update request
      updatePostRequest = (event) => {
        fetch(`/api/v1/posts/${this.state.id}`, {
          method: 'put',
          body: JSON.stringify(this.state),
          headers: { 'Content-Type': 'application/json' },
        }).then((response) => {
          alert('Post updated successfully');
          location.href = '/';
        });
      }

    
    render() { 
        
          console.log('dd', this.props.items);
          
         const columns = [
            {
              title:  I18n.t("item.itemName"),
              dataIndex: 'name',
              filters: [
                {
                  text: 'Joe',
                  value: 'Joe',
                },
                {
                  text: 'Jim',
                  value: 'Jim',
                },
                {
                  text: 'Submenu',
                  value: 'Submenu',
                  children: [
                    {
                      text: 'Green',
                      value: 'Green',
                    },
                    {
                      text: 'Black',
                      value: 'Black',
                    },
                  ],
                },
              ],
              // specify the condition of filtering result
              // here is that finding the name started with `value`
              onFilter: (value, record) => record.name.indexOf(value) === 0,
              sorter: (a, b) => a.name.length - b.name.length,
              sortDirections: ['descend'],
            },
            {
              title: I18n.t("item.itemPrice"),
              dataIndex: 'price',
              defaultSortOrder: 'descend',
              sorter: (a, b) => a.age - b.age,
            },
    
            {
                title: I18n.t("item.itemSize"),
                dataIndex: 'size',
                defaultSortOrder: 'descend',
                sorter: (a, b) => a.age - b.age,
              },
    
            {
                title: I18n.t("item.itemCountry"),
                dataIndex: 'country',
                defaultSortOrder: 'descend',
                sorter: (a, b) => a.age - b.age,
              },
    
            {
              title: I18n.t("item.itemDate"),
              dataIndex: 'manafacture_date',
              filters: [
                {
                  text: 'London',
                  value: 'London',
                },
                {
                  text: 'New York',
                  value: 'New York',
                },
              ],
              onFilter: (value, record) => record.address.indexOf(value) === 0,
            },
            {
              title: "",
              key: "action",
              render: (_text, record) => (
                <Fragment>
                <Popconfirm title="Are you sure to delete this item?" onConfirm={() => this.props.delete(record.id)} okText="Yes" cancelText="No">
                  <button type="danger" style={{color: red}}>
                  {I18n.t("common.button.delete")}{" "}
                  </button>
                </Popconfirm>
                <button type="primary" onClick = {() => this.props.edit(record)} style={{marginLeft: 12, color: primary}}>
                {I18n.t("common.button.edit")}{" "}
                </button>
                </Fragment>
              ),
            },
            
          ];
          
          function onChange(pagination, filters, sorter, extra) {
            console.log('params', pagination, filters, sorter, extra);
          }

          
        return(
        <div>
            <Table columns={columns} dataSource={this.props.items} />
        </div>
        );
    }
}
 
export default ItemTable;
