import React, { useState } from "react";
import { Form, Input, Button, Select, DatePicker, InputNumber, Modal, Radio, Table } from "antd";
import I18n from "i18n-js";
const { Search } = Input;
import moment from "moment";
const ItemForm = ({ onFinish, componentEdit, itemSize, company }) => {
  // validateMessages = {
  //   number: {
  //     range: '${price} ${min} and ${max}文字である必要があります'
  //   },
  // };
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [tableData, setColumnData] = useState(null);
  const [selectionType, setSelectionType] = useState('radio');
  const companies = [];
  const onChange = (value) => {
    componentEdit.current?.setFieldsValue({
      company_name: value.company_name,
      company_id: value.id
    });
  };
  
  const showModal = () => {
    setIsModalVisible(true);
    setColumnData(company);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    componentEdit.current?.setFieldsValue({
      company_name: '一つ選択してください'
    });
    setIsModalVisible(false);
  };

  const disabledDate = (current) => {
    // Can not select days before today and today
    return current < moment().endOf('day');
  };

  const searchField =
        <Input
          key = '1'
          name = 'searchData'
          onChange = { (e) => handleSearch(e.target.value ? [e.target.value] : []) }>
        </Input>

  const handleSearch = (searchData) =>{
    console.log('searchData', searchData);
    for (var i = 0; i < company.length; i++) {
      if (
        company[i].company_name
          .toLowerCase()
          .includes(searchData.toString().toLowerCase())
      ) {
        companies.push(company[i]);
      }
    }
    setColumnData(companies);
  };

  // const handleShow = () => {
  //   console.log('companyLength------', companies);
  //   if (!companies.length) {
  //     console.log('searchData------');
  //     {/* condition for show all company data and search data */ }
  //     return (<Radio.Group name="radiogroup">
  //       {company.map((value) => (
  //        <Radio key = { value.id } value = { value.id } onChange = { () => onChange(value) }　> { value.company_name } </Radio>
  //       ))}
  //     </Radio.Group>
  //     );
  //   }
  //   else {
  //     console.log('all data-----');
  //     return (
  //       <Radio.Group name="radiogroup">
  //         {companies.map((value) => (
  //           <Radio key = { value.id } value = { value.id } onChange = { () => onChange(value) }　>{value.company_name}</Radio>
  //         ))}
  //       </Radio.Group>
  //     );
  //   }
  // };
  
  const columns = [
    {
      title: I18n.t("item.company"),
      dataIndex: 'company_name'
    }];
  
  return (
    <div>
      {/* form setting */}
      <Form
        labelCol = {{ span: 6 }}
        wrapperCol = {{ span: 14 }}
        layout = "horizontal"
        onFinish = { onFinish }
        ref = { componentEdit }
      >
        {/* 番号フィールド */}
        <Form.Item label = " " name="id" style = {{ display: "none" }}>
          <Input />
        </Form.Item>

        {/* アイテム名前フィールド */}
        <Form.Item
          label = { I18n.t("item.itemName") }
          name = "name"
          rules = {[
            {
              required: true,
              message: "この項目は必須です。",
            },
          ]}
        >
          <Input style = {{ width: "60%" }} />
        </Form.Item>
          
        {/* アイテム価格フィールド */}
        <Form.Item
          label = { I18n.t("item.itemPrice") }
          name = "price"
          rules = {[
            {
              required: true,
              message: "この項目は必須です。",
            }
          ]}
        >
          <InputNumber style = {{ width: "60%" }} />
        </Form.Item>
          
        {/* アイテムサイズフィールド */}
        <Form.Item
          label = { I18n.t("item.itemSize") }
          name = "size_id"
          rules = {[
            {
              required: true,
              message: "この項目は必須です。",
            },
          ]}
        >
          <Select defaultValue = "一つ選択してください" style = {{ width: "60%" }}>
            {itemSize.map((value) => (
              <Select.Option key = { value.id } value = { value.id }>{ value.size }</Select.Option>
            ))}
          </Select>
        </Form.Item>
          
        {/* アイテムの製造国フィールド */}
        <Form.Item
          label = { I18n.t("item.itemCountry") }
          name = "country"
          rules = {[
            {
              required: true,
              message: "この項目は必須です。",
            },
          ]}
        >
          <Input style = {{ width: "60%" }} />
        </Form.Item>
          
          {/* company list field */}
        <Form.Item
          label = { I18n.t("item.company") }
          name = "company_name"
          rules = {[
            {
              required: true,
              message: "この項目は必須です。",
            },
          ]}
        >
         <Search
          allowClear
          enterButton = "検索"
          size = "large"
          style = {{ width: "60%" }}
          onSearch = { showModal }
        />
        </Form.Item>
          
        {/* 製造日フィールド */}
        <Form.Item
          label = { I18n.t("item.itemDate") }
          name = "manafacture_date"
          rules = {[
            {
              required: true,
              message: "この項目は必須です。",
            },
          ]}
        >
          <DatePicker disabledDate = { disabledDate } style = {{ width: "60%" }} placeholder = '日付を選択してください'/>
        </Form.Item>

        <Form.Item
          label = { I18n.t("item.email") }
          name="email"
          rules = {[
            {
              type: 'email',
              message: "正しいメールアドレスに記入してください。",
            },
            {
              required: true,
              message: "この項目は必須です。",
            },
          ]}>
          <Input stylec = {{ width: "60%" }} />
        </Form.Item>
          
        <Form.Item
          label = { I18n.t("item.remark") }
          name = "remark"
          rules = {[
            {
              max: 20,
              message: "20文字以内を入力してください。",
            }
          ]}
        >
          <Input.TextArea style = {{ width: "60%", resize: "none" }} />
        </Form.Item>

        {/* carry company id */}
        <Form.Item label = " " name = "company_id" style = {{ display: "none" }}>
          <Input />
        </Form.Item>

        {/* carry size id */}
        {/* <Form.Item label=" " name="size_id" style={{ display: "none" }}>
          <Input />
        </Form.Item> */}

        {/* 保存ボタン */}
        <Form.Item label = " ">
          <Button type = "primary" htmlType = "submit">
            { I18n.t("common.button.save") }
          </Button>
        </Form.Item>
      </Form>

      {/* popup Modal */}
      
        <Modal title = "会社一連" visible = { isModalVisible } 
        onOk = { handleOk } onCancel = { handleCancel }
        // footer={[
        //     <Button onClick={handleCancel}>
        //       オーケー
        //     </Button>,
        //   ]}
          >
        {/* call search input field */}
         { searchField }
        {/* { handleShow() } */}
        <Table
          rowSelection = {{
            type: selectionType
          }}
          columns = { columns }
          dataSource = { tableData }
          pagination = {{ pageSize: 5 }}
        />
        </Modal>
    </div>
  );
};

export default ItemForm;
