import React, { Fragment } from "react";
import { Table, message, Popconfirm, Button } from "antd";
import I18n from "i18n-js";
class ItemTable extends React.Component {
  render() {
    const columns = [
      {
        title: I18n.t("item.itemName"),
        dataIndex: "name",
        key: "name",
        ...this.props.filter("name"),
        sorter: (a, b) => a.name.localeCompare(b.name),
        sortDirections: ["descend", "ascend"],
      },

      {
        title: I18n.t("item.itemPrice"),
        dataIndex: "price",
        key: "price",
        ...this.props.filter("price"),
        sorter: (a, b) => a.price.localeCompare(b.price),
        sortDirections: ["descend", "ascend"],
      },

      {
        title: I18n.t("item.itemSize"),
        dataIndex: "size",
        key: "size_id",
        ...this.props.filter("size_id"),
        sorter: (a, b) => a.size_id.localeCompare(b.size_id),
        sortDirections: ["descend", "ascend"],
      },

      {
        title: I18n.t("item.company"),
        dataIndex: "company",
        key: "company_id",
        ...this.props.filter("company_id"),
        sorter: (a, b) => a.company_id.localeCompare(b.company_id),
        sortDirections: ["descend", "ascend"],
      },

      {
        title: I18n.t("item.itemCountry"),
        dataIndex: "country",
        key: "country",
        ...this.props.filter("country"),
        sorter: (a, b) => a.country.localeCompare(b.country),
        sortDirections: ["descend", "ascend"],
      },

      // {
      //   title: I18n.t("item.itemDate"),
      //   dataIndex: "manafacture_date",
      // },

      {
        title: I18n.t("item.email"),
        dataIndex: "email",
        key: "email",
        ...this.props.filter("email"),
      },

      {
        title: I18n.t("item.remark"),
        dataIndex: "remark",
      },

      {
        title: "",
        key: "action",
        render: (_text, record) => (
          <Fragment>
            {/* アイテムー管理テーブルリストに表示するアイテムデータを消すかどうか確認する機能 */}
            <Popconfirm
              title="削除してもよろしいですか？"
              onConfirm={() => this.props.delete(record.id)}
              okText="はい"
              cancelText="いいえ"
            >
              {/* アイテムー管理テーブルリストに表示するアイテムデータを消すボタン */}
              <Button type="danger"> {I18n.t("common.button.delete")} </Button>
            </Popconfirm>

            {/* 更新したいデータを更新フォームに設定するボタン */}
            <Button
              onClick={() => this.props.edit(record)}
              style={{ marginLeft: 12 }}
              type="primary"
            >
              {I18n.t("common.button.edit")}{" "}
            </Button>
          </Fragment>
        ),
      },
    ];

    return (
      <div>
        {/* 表示テーブル */}
        <Table
          style={{ whiteSpace: "pre" }}
          columns={columns}
          dataSource={this.props.items}
        />
      </div>
    );
  }
}

export default ItemTable;
